var Greeter=function(){function t(t){this.element=t,this.element.innerHTML+="The time is: ",this.span=document.createElement("span"),this.element.appendChild(this.span),this.span.innerText=(new Date).toUTCString()}return t.prototype.start=function(){var t=this;this.timerToken=setInterval(function(){return t.span.innerHTML=(new Date).toUTCString()},500)},t.prototype.stop=function(){clearTimeout(this.timerToken)},t}();window.onload=function(){var t=document.getElementById("content"),e=new Greeter(t);e.start()},alert("hello world");